import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";

class App extends Component {
  render() {
    const data = [{
      serial: 1,
      perk: 'Air-condition',
      id: 'click-me-button',
      render: ({ row }) => (<button onClick={(e) => this.handleButtonClick(e, row)}>Click Me</button>)

    },{
      serial: 2,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 3,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 4,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 5,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 1,
      perk: 'Air-condition',
      action:'delete'
    }]
    const columns = [{
      Header: 'Serial No',
      accessor: 'serial'
    },{
      Header: 'Perk Name',
      accessor: 'perk'
    },{
      Header: 'Action',
      accessor: 'action'
    }]
    return (
        <div>
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {2}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
