import React from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import './App.css';
import Form from './AddFolder/Form';



const App  = (props) => {

    const data = [{
        serial: 1,
        perk: 'Air-condition',
        action:'delete'
    },{
        serial: 2,
        perk: 'Water',
        action:'delete'
    },{
        serial: 3,
        perk: 'Snacks',
        action:'delete'
    },{
        serial: 4,
        perk: 'Newspaper',
        action:'delete'
    },{
        serial: 5,
        perk: 'Air-condition',
        action:'delete'
    },{
        serial: 6,
        perk: 'Air-condition',
        action:'delete'
    }];
    const columns = [{
        Header: 'Serial No',
        accessor: 'serial'
    },{
        Header: 'Perk Name',
        accessor: 'perk'
    },{
        Header: 'Action',
        Cell: row => (
            <div>
                <button className="mr-3 bg-success">Active</button>
                <button className="mr-3 bg-warning">Edit</button>
                <button className="mr-3 bg-danger onClick={() => handleDelete(row.original)}">Delete</button>
            </div>
        )
    }];

    const handleSubmit = e => {
        e.preventDefault();
        data.push({
            serial: 7,
            perk: e.target.value,
            action:'delete'
        })
        console.log (data)
    };

    return (

        <div>
          <Form handleSubmit={handleSubmit}/>
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {6}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
};
export default App;
