import React, { useState } from 'react';


const Form = (addValue) => {
    const [value, setValue] = useState();

    // const handleSubmit = e => {
    //     console.log('submitting bla bla bal')
    //     e.preventDefault();
    //    const value = e.target.element.value.value;
    //
    //    if (item) {
    //        Form.value.push(value);
    //        e.target.element.value.value = '';
    //    }
    // }
    const handleSubmit = e => {
        e.preventDefault();
        if(!value) return;
        addValue(value);
        setValue('');
    }

    return (
        <form className="inline" onSubmit={handleSubmit}>
            <div>
                <label className=" ml-3 mr-3 mt-3 mb-5">Name: </label>
                <input type="text" name="value" value={value} onChange={e => setValue(e.target.value)}/>
                <button type="submit" className="ml-3 border-0 btn btn-sm btn-info">Submit</button>
            </div>
        </form>
    );
}

export default Form;
