import React, { Component, useState } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import './App.css';
import Form from './AddFolder/Form';
import AddData from './AddFolder/Form';



class App extends Component {
  render() {
    const data = []
    const columns = [{
      Header: 'Serial No',
      accessor: 'serial'
    },{
      Header: 'Perk Name',
      accessor: 'perk'
    },{
      Header: 'Action',
      Cell: row => (
          <div>
            <button className="mr-3 bg-success">Active</button>
            <button className="mr-3 bg-warning">Edit</button>
            <button className="mr-3 bg-danger onClick={() => handleDelete(row.original)}">Delete</button>
          </div>
      )
    }]
    return (

        <div>
          <Form />
          <ReactTable
              data={AddData}
              columns={columns}
              defaultPageSize = {6}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
