import React, { useState } from 'react';


const Form = (item) => {
    const [value, setValue] = useState();

    const handleSubmit = e => {
        console.log('submitting bla bla bal')
        e.preventDefault();
       const item = e.target.element.item.value;

       if (item) {
           Form.item.push(item);
           e.target.element.item.value = '';
       }
    }

    return (
        <form className="inline" onSubmit={handleSubmit}>
            <div>
                <label className=" ml-3 mr-3 mt-3 mb-5">Name: </label>
                <input type="text" name="item" value={value} onChange={e => setValue(e.target.value)}/>
                <button type="submit" className="ml-3 border-0 btn btn-sm btn-info">Submit</button>
            </div>
        </form>
    );
}

export default Form;
