import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";

class App extends Component {
  render() {
    const data = [{
      SerialNo: 1,
      PerkName: 'Air-condition',
        Action:'delete'
    },{
      SerialNo: 1,
      PerkName: 'Air-condition',
      Action:'delete'
    },{
      SerialNo: 1,
      PerkName: 'Air-condition',
      Action:'delete'
    },{
      SerialNo: 1,
      PerkName: 'Air-condition',
      Action:'delete'
    },{
      SerialNo: 1,
      PerkName: 'Air-condition',
      Action:'delete'
    },{
      SerialNo: 1,
      PerkName: 'Air-condition',
      Action:'delete'
    }]
    const columns = [{
      Header: 'S/N',
      accessor: 's/n'
    },{
      Header: 'Perk Name',
      accessor: 'perk name'
    },]
    return (
        <div>
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {2}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
