import React, { useEffect, useState } from 'react';
const Form = (addValue) => {
    const [value, setValue] = useState();
    const [items, SetItem] = useState(['']);
    const handleSubmit = e => {
        console.log('submiting here')
        e.preventDefault();
        if(!value) return;
        addValue(value);
        setValue();
    }
    useEffect(() =>{
        console.log(
            value
        )
    },[value]);
    return (
        <form className="inline" onSubmit={handleSubmit}>
            <div>
                <label className=" ml-3 mr-3 mt-3 mb-5">Name: </label>
                <input type="input" value={value} onChange={e => setValue(e.target.value)}/>
                <button type="submit" className="ml-3 border-0 btn btn-sm btn-info">Submit</button>
            </div>
        </form>
    );
}

export default Form;
