import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";

class App extends Component {
  render() {
    const data = [{
      serial: 1,
      perk: 'Air-condition',
      action:'\n' +
          '       Cell: row => (\n' +
          '           <div>\n' +
          '               <button onClick={() => handleEdit(row.original)}>Edit</button>\n' +
          '               <button onClick={() => handleDelete(row.original)}>Delete</button>\n' +
          '           </div>\n' +
          '       )'

    },{
      serial: 2,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 3,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 4,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 5,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 1,
      perk: 'Air-condition',
      action:'delete'
    }]
    const columns = [{
      Header: 'Serial No',
      accessor: 'serial'
    },{
      Header: 'Perk Name',
      accessor: 'perk'
    },{
      Header: 'Action',
      accessor: 'action'
    }]
    return (
        <div>
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {2}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
