import React, { Component, useState } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import './App.css';
import Form from './AddFolder/Form';



class App extends Component {
  render() {

      const [value, setvalue] = useState = ('');
    const data = []
    const columns = [{
      Header: 'Serial No',
      accessor: 'serial'
    },{
      Header: 'Perk Name',
      accessor: 'perk'
    },{
      Header: 'Action',
      Cell: row => (
          <div>
            <button className="mr-3 bg-success">Active</button>
            <button className="mr-3 bg-warning">Edit</button>
            <button className="mr-3 bg-danger onClick={() => handleDelete(row.original)}">Delete</button>
          </div>
      )
    }]
      const addvalue = (props) => {
          const newValue = [...value, {props}];
          setvalue(newValue);
      };
    return (

        <div>
          <Form />
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {6}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
