import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";

class App extends Component {
  render() {
    const data = [{
      serial: 1,
      perk: 'Air-condition',
      Cell: row => (
          <div>
            <button>Active</button>
            <button>Edit</button>
            <button>Delete</button>
          </div>
      )
    },
      {
        id: 'edit',
        accessor: '[row identifier to be passed to button]',
        Cell: ({value}) => (<button onClick={this.editRow({value})}>Edit</button>)
      },
      {
      serial: 2,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 3,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 4,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 5,
      perk: 'Air-condition',
      action:'delete'
    },{
      serial: 1,
      perk: 'Air-condition',
      action:'delete'
    }]
    const columns = [{
      Header: 'Serial No',
      accessor: 'serial'
    },{
      Header: 'Perk Name',
      accessor: 'perk'
    },{
      Header: 'Action',
      accessor: 'action'
    }]
    return (
        <div>
          <ReactTable
              data={data}
              columns={columns}
              defaultPageSize = {2}
              pageSizeOptions = {[2,4, 6]}
          />
        </div>
    )
  }
}
export default App;
